import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Example state, replace with your authentication logic
  // You may want to check localStorage or cookies for token validity here on initial load

  const login = (token) => {
    localStorage.setItem('token', token); // Example: Storing token in localStorage
    setIsLoggedIn(true);
  };

  const logout = () => {
    localStorage.removeItem('token'); // Example: Clearing token from localStorage
    setIsLoggedIn(false);
  };

  return (
    <AuthContext.Provider value={{ isLoggedIn, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};


