import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './components/AuthContext';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/Home';
import Login from './components/Login';
import Signup from './components/Signup';
import Logout from './components/Logout';
import PrivateRoute from './components/PrivateRoute';
import RecipeForm from './components/RecipeForm';
import RecipeList from './components/RecipeList';
import RecipeDetail from './components/RecipeDetail';
import './App.css';

const AuthWrapper = ({ children }) => {
  const { isLoggedIn } = useAuth();

  if (isLoggedIn) {
    return <Navigate to="/recipes" replace />;
  }

  return children;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Header />
          <Routes>
            <Route path="/" element={<AuthWrapper><Home /></AuthWrapper>} />
            <Route path="/login" element={<AuthWrapper><Login /></AuthWrapper>} />
            <Route path="/signup" element={<AuthWrapper><Signup /></AuthWrapper>} />
            <Route path="/logout" element={<Logout />} />
            <Route element={<PrivateRoute />}>
              <Route path="/submit-recipe" element={<RecipeForm />} />
              <Route path="/recipes" element={<RecipeList />} />
              <Route path="/recipe/:id" element={<RecipeDetail />} />
            </Route>
          </Routes>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;



